{
  "name": "@conway/automaton-cli",
  "version": "0.1.0",
  "description": "Conway Automaton - Creator CLI",
  "type": "module",
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "bin": {
    "automaton-cli": "dist/index.js"
  },
  "scripts": {
    "build": "tsc",
    "dev": "tsx watch src/index.ts",
    "clean": "rm -rf dist"
  },
  "dependencies": {
    "@conway/automaton": "workspace:*",
    "chalk": "^5.3.0",
    "viem": "^2.44.2"
  },
  "devDependencies": {
    "@types/node": "^20.10.0",
    "tsx": "^4.7.0",
    "typescript": "^5.3.0"
  }
}

/**
 * automaton-cli fund <amount> [--to 0x...]
 *
 * Transfer Conway credits using the configured Conway API key.
 */

import { loadConfig } from "@conway/automaton/config.js";

const args = process.argv.slice(3);
const amount = args[0];
const toIndex = args.indexOf("--to");
const toAddress = toIndex >= 0 ? args[toIndex + 1] : undefined;

if (!amount) {
  console.log("Usage: automaton-cli fund <amount> [--to 0x...]");
  console.log("Examples:");
  console.log("  automaton-cli fund 5.00");
  console.log("  automaton-cli fund 500 --to 0xabc...");
  process.exit(1);
}

const config = loadConfig();
if (!config) {
  console.log("No automaton configuration found.");
  process.exit(1);
}

if (!config.conwayApiKey) {
  console.log("No Conway API key found in automaton config.");
  process.exit(1);
}

const amountCents = parseAmountToCents(amount);
if (amountCents <= 0) {
  console.log(`Invalid amount: ${amount}`);
  process.exit(1);
}

const destination = toAddress || config.walletAddress;

const payload = {
  to_address: destination,
  amount_cents: amountCents,
  note: `fund via automaton-cli (${config.name})`,
};

const apiUrl = config.conwayApiUrl || "https://api.conway.tech";
const paths = ["/v1/credits/transfer", "/v1/credits/transfers"];

let success: any | null = null;
let lastError = "";

for (const path of paths) {
  const resp = await fetch(`${apiUrl}${path}`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: config.conwayApiKey,
    },
    body: JSON.stringify(payload),
  });

  if (!resp.ok) {
    const text = await resp.text();
    lastError = `${resp.status}: ${text}`;
    if (resp.status === 404) {
      continue;
    }
    console.log(`Credit transfer failed (${path}): ${lastError}`);
    process.exit(1);
  }

  success = await resp.json().catch(() => ({}));
  break;
}

if (!success) {
  console.log(`Credit transfer failed: ${lastError || "unknown error"}`);
  process.exit(1);
}

const transferId = success.transfer_id || success.id || "n/a";
const status = success.status || "submitted";
const balanceAfter = success.balance_after_cents ?? success.new_balance_cents;

console.log(`
Transfer submitted.
From key:  ${maskKey(config.conwayApiKey)}
To:        ${destination}
Amount:    $${(amountCents / 100).toFixed(2)} (${amountCents} cents)
Status:    ${status}
Transfer:  ${transferId}
${balanceAfter !== undefined ? `Balance:   $${(Number(balanceAfter) / 100).toFixed(2)} after transfer` : ""}
`);

function parseAmountToCents(raw: string): number {
  const trimmed = raw.trim();
  if (!trimmed) return 0;

  // If user provides integer >= 100, treat as cents.
  if (/^\d+$/.test(trimmed) && Number(trimmed) >= 100) {
    return Number(trimmed);
  }

  const dollars = Number(trimmed);
  if (!Number.isFinite(dollars)) return 0;
  return Math.round(dollars * 100);
}

function maskKey(key: string): string {
  if (key.length < 8) return "***";
  return `${key.slice(0, 4)}...${key.slice(-4)}`;
}

/**
 * automaton-cli logs
 *
 * View the automaton's turn log.
 */

import { loadConfig, resolvePath } from "@conway/automaton/config.js";
import { createDatabase } from "@conway/automaton/state/database.js";

const args = process.argv.slice(3);
let limit = 20;
const tailIdx = args.indexOf("--tail");
if (tailIdx !== -1 && args[tailIdx + 1]) {
  limit = parseInt(args[tailIdx + 1], 10) || 20;
}

const config = loadConfig();
if (!config) {
  console.log("No automaton configuration found.");
  process.exit(1);
}

const dbPath = resolvePath(config.dbPath);
const db = createDatabase(dbPath);

const turns = db.getRecentTurns(limit);

if (turns.length === 0) {
  console.log("No turns recorded yet.");
} else {
  for (const turn of turns) {
    console.log(`\n--- Turn ${turn.id} [${turn.timestamp}] state:${turn.state} ---`);
    if (turn.input) {
      console.log(`Input (${turn.inputSource}): ${turn.input.slice(0, 200)}`);
    }
    console.log(`Thinking: ${turn.thinking.slice(0, 500)}`);
    if (turn.toolCalls.length > 0) {
      console.log("Tools:");
      for (const tc of turn.toolCalls) {
        console.log(
          `  ${tc.name}: ${tc.error ? `ERROR: ${tc.error}` : tc.result.slice(0, 100)}`,
        );
      }
    }
    console.log(
      `Tokens: ${turn.tokenUsage.totalTokens} | Cost: $${(turn.costCents / 100).toFixed(4)}`,
    );
  }
}

db.close();

/**
 * automaton-cli send <to-address> "message text"
 *
 * Send a message to an automaton or address via the social relay.
 */

import { loadConfig } from "@conway/automaton/config.js";
import { privateKeyToAccount } from "viem/accounts";
import fs from "fs";
import path from "path";

const args = process.argv.slice(3);
const toAddress = args[0];
const messageText = args.slice(1).join(" ");

if (!toAddress || !messageText) {
  console.log("Usage: automaton-cli send <to-address> <message>");
  console.log("Examples:");
  console.log('  automaton-cli send 0xabc...def "Hello, fellow automaton!"');
  process.exit(1);
}

// Load wallet
const walletPath = path.join(
  process.env.HOME || "/root",
  ".automaton",
  "wallet.json",
);

if (!fs.existsSync(walletPath)) {
  console.log("No wallet found at ~/.automaton/wallet.json");
  console.log("Run: automaton --init");
  process.exit(1);
}

const walletData = JSON.parse(fs.readFileSync(walletPath, "utf-8"));
const account = privateKeyToAccount(walletData.privateKey as `0x${string}`);

// Load config for relay URL
const config = loadConfig();
const relayUrl =
  config?.socialRelayUrl ||
  process.env.SOCIAL_RELAY_URL ||
  "https://social.conway.tech";

try {
  const resp = await fetch(`${relayUrl}/v1/messages`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      from: account.address,
      to: toAddress,
      content: messageText,
      signed_at: new Date().toISOString(),
    }),
  });

  if (!resp.ok) {
    throw new Error(`Relay returned ${resp.status}: ${await resp.text()}`);
  }

  const result = (await resp.json()) as { id?: string };
  console.log(`Message sent.`);
  console.log(`  ID:   ${result.id || "n/a"}`);
  console.log(`  From: ${account.address}`);
  console.log(`  To:   ${toAddress}`);
  console.log(`  Relay: ${relayUrl}`);
} catch (err: any) {
  console.error(`Failed to send message: ${err.message}`);
  process.exit(1);
}

/**
 * automaton-cli status
 *
 * Show the current status of an automaton.
 */

import { loadConfig, resolvePath } from "@conway/automaton/config.js";
import { createDatabase } from "@conway/automaton/state/database.js";

const config = loadConfig();
if (!config) {
  console.log("No automaton configuration found.");
  process.exit(1);
}

const dbPath = resolvePath(config.dbPath);
const db = createDatabase(dbPath);

const state = db.getAgentState();
const turnCount = db.getTurnCount();
const tools = db.getInstalledTools();
const heartbeats = db.getHeartbeatEntries();
const recentTurns = db.getRecentTurns(5);

console.log(`
=== ${config.name} ===
Address:    ${config.walletAddress}
Creator:    ${config.creatorAddress}
Sandbox:    ${config.sandboxId}
State:      ${state}
Turns:      ${turnCount}
Tools:      ${tools.length} installed
Heartbeats: ${heartbeats.filter((h) => h.enabled).length} active
Model:      ${config.inferenceModel}
`);

if (recentTurns.length > 0) {
  console.log("Recent activity:");
  for (const turn of recentTurns) {
    const tools = turn.toolCalls.map((t) => t.name).join(", ");
    console.log(
      `  [${turn.timestamp}] ${turn.thinking.slice(0, 80)}...${tools ? ` (tools: ${tools})` : ""}`,
    );
  }
}

db.close();

#!/usr/bin/env node
/**
 * Conway Automaton CLI
 *
 * Creator-facing CLI for interacting with an automaton.
 * Usage: automaton-cli <command> [args]
 */

const args = process.argv.slice(2);
const command = args[0];

async function main(): Promise<void> {
  switch (command) {
    case "status":
      await import("./commands/status.js");
      break;
    case "logs":
      await import("./commands/logs.js");
      break;
    case "fund":
      await import("./commands/fund.js");
      break;
    case "send":
      await import("./commands/send.js");
      break;
    default:
      console.log(`
Conway Automaton CLI - Creator Tools

Usage:
  automaton-cli status              Show automaton status
  automaton-cli logs [--tail N]     View automaton logs
  automaton-cli fund <amount> [--to 0x...]  Transfer Conway credits
  automaton-cli send <to-address> <message> Send a social message
`);
  }
}

main().catch((err) => {
  console.error(`Error: ${err.message}`);
  process.exit(1);
});

declare const process: {
  argv: string[];
  exit(code?: number): never;
  env: Record<string, string | undefined>;
};

declare module "@conway/automaton/config.js" {
  export interface AutomatonCliConfig {
    name: string;
    walletAddress: string;
    creatorAddress: string;
    sandboxId: string;
    dbPath: string;
    inferenceModel: string;
    conwayApiUrl: string;
    conwayApiKey: string;
    socialRelayUrl?: string;
  }

  export function loadConfig(): AutomatonCliConfig | null;
  export function resolvePath(p: string): string;
}

declare module "@conway/automaton/state/database.js" {
  export interface CliToolCall {
    name: string;
    result: string;
    error?: string;
  }

  export interface CliTurn {
    id: string;
    timestamp: string;
    state: string;
    input?: string;
    inputSource?: string;
    thinking: string;
    toolCalls: CliToolCall[];
    tokenUsage: { totalTokens: number };
    costCents: number;
  }

  export interface CliHeartbeatEntry {
    enabled: boolean;
  }

  export interface CliInstalledTool {
    id: string;
    name: string;
  }

  export interface AutomatonCliDatabase {
    getAgentState(): string;
    getTurnCount(): number;
    getInstalledTools(): CliInstalledTool[];
    getHeartbeatEntries(): CliHeartbeatEntry[];
    getRecentTurns(limit: number): CliTurn[];
    close(): void;
  }

  export function createDatabase(path: string): AutomatonCliDatabase;
}

