      }),
    );
    process.exit(0);
  }

  if (args.includes("--provision")) {
    try {
      const result = await provision();
      console.log(JSON.stringify(result));
    } catch (err: any) {
      console.error(`Provision failed: ${err.message}`);
      process.exit(1);
    }
    process.exit(0);
  }

  if (args.includes("--status")) {
    await showStatus();
    process.exit(0);
  }

  if (args.includes("--setup")) {
    const { runSetupWizard } = await import("./setup/wizard.js");
    await runSetupWizard();
    process.exit(0);
  }

  if (args.includes("--run")) {
    await run();
    return;
  }

  // Default: show help
  console.log('Run "automaton --help" for usage information.');
  console.log('Run "automaton --run" to start the automaton.');
}

// ─── Status Command ────────────────────────────────────────────

async function showStatus(): Promise<void> {
  const config = loadConfig();
  if (!config) {
    console.log("Automaton is not configured. Run the setup script first.");
    return;
  }

  const dbPath = resolvePath(config.dbPath);
  const db = createDatabase(dbPath);

  const state = db.getAgentState();
  const turnCount = db.getTurnCount();
  const tools = db.getInstalledTools();
  const heartbeats = db.getHeartbeatEntries();
  const skills = db.getSkills(true);
  const children = db.getChildren();
  const registry = db.getRegistryEntry();

  console.log(`
=== AUTOMATON STATUS ===
Name:       ${config.name}
Address:    ${config.walletAddress}
Creator:    ${config.creatorAddress}
Sandbox:    ${config.sandboxId}
State:      ${state}
Turns:      ${turnCount}
Tools:      ${tools.length} installed
Skills:     ${skills.length} active
Heartbeats: ${heartbeats.filter((h) => h.enabled).length} active
Children:   ${children.filter((c) => c.status !== "dead").length} alive / ${children.length} total
Agent ID:   ${registry?.agentId || "not registered"}
Model:      ${config.inferenceModel}
Version:    ${config.version}
========================
`);

  db.close();
}

// ─── Main Run ──────────────────────────────────────────────────

async function run(): Promise<void> {
  console.log(`[${new Date().toISOString()}] Conway Automaton v${VERSION} starting...`);

  // Load config — first run triggers interactive setup wizard
  let config = loadConfig();
  if (!config) {
    const { runSetupWizard } = await import("./setup/wizard.js");
    config = await runSetupWizard();
  }

  // Load wallet
  const { account } = await getWallet();
  const apiKey = config.conwayApiKey || loadApiKeyFromConfig();
  if (!apiKey) {
    console.error(
      "No API key found. Run: automaton --provision",
    );
    process.exit(1);
  }

  // Build identity
  const identity: AutomatonIdentity = {
    name: config.name,
    address: account.address,
    account,
    creatorAddress: config.creatorAddress,
    sandboxId: config.sandboxId,
    apiKey,
    createdAt: new Date().toISOString(),
  };

  // Initialize database
  const dbPath = resolvePath(config.dbPath);
  const db = createDatabase(dbPath);

  // Store identity in DB
  db.setIdentity("name", config.name);
  db.setIdentity("address", account.address);
  db.setIdentity("creator", config.creatorAddress);
  db.setIdentity("sandbox", config.sandboxId);

  // Create Conway client
  const conway = createConwayClient({
    apiUrl: config.conwayApiUrl,
    apiKey,
    sandboxId: config.sandboxId,
  });

  // Create inference client
  const inference = createInferenceClient({
    apiUrl: config.conwayApiUrl,
    apiKey,
    defaultModel: config.inferenceModel,
    maxTokens: config.maxTokensPerTurn,
  });

  // Create social client
  let social: SocialClientInterface | undefined;
  if (config.socialRelayUrl) {
    social = createSocialClient(config.socialRelayUrl, account);
    console.log(`[${new Date().toISOString()}] Social relay: ${config.socialRelayUrl}`);
  }

  // Load and sync heartbeat config
  const heartbeatConfigPath = resolvePath(config.heartbeatConfigPath);
  const heartbeatConfig = loadHeartbeatConfig(heartbeatConfigPath);
  syncHeartbeatToDb(heartbeatConfig, db);

  // Load skills
  const skillsDir = config.skillsDir || "~/.automaton/skills";
  let skills: Skill[] = [];
  try {
    skills = loadSkills(skillsDir, db);
    console.log(`[${new Date().toISOString()}] Loaded ${skills.length} skills.`);
  } catch (err: any) {
    console.warn(`[${new Date().toISOString()}] Skills loading failed: ${err.message}`);
  }

  // Initialize state repo (git)
  try {
    await initStateRepo(conway);
    console.log(`[${new Date().toISOString()}] State repo initialized.`);
  } catch (err: any) {
    console.warn(`[${new Date().toISOString()}] State repo init failed: ${err.message}`);
  }

  // Start heartbeat daemon
  const heartbeat = createHeartbeatDaemon({
    identity,
    config,
    db,
    conway,
    social,
    onWakeRequest: (reason) => {
      console.log(`[HEARTBEAT] Wake request: ${reason}`);
      // The heartbeat can trigger the agent loop
      // In the main run loop, we check for wake requests
      db.setKV("wake_request", reason);
    },
  });

  heartbeat.start();
  console.log(`[${new Date().toISOString()}] Heartbeat daemon started.`);

  // Handle graceful shutdown
  const shutdown = () => {
    console.log(`[${new Date().toISOString()}] Shutting down...`);
    heartbeat.stop();
    db.setAgentState("sleeping");
    db.close();
    process.exit(0);
  };

  process.on("SIGTERM", shutdown);
  process.on("SIGINT", shutdown);

  // ─── Main Run Loop ──────────────────────────────────────────
  // The automaton alternates between running and sleeping.
  // The heartbeat can wake it up.

  while (true) {
    try {
      // Reload skills (may have changed since last loop)
      try {
        skills = loadSkills(skillsDir, db);
      } catch {}

      // Run the agent loop
      await runAgentLoop({
        identity,
        config,
        db,
        conway,
        inference,
        social,
        skills,
        onStateChange: (state: AgentState) => {
          console.log(`[${new Date().toISOString()}] State: ${state}`);
        },
        onTurnComplete: (turn) => {
          console.log(
            `[${new Date().toISOString()}] Turn ${turn.id}: ${turn.toolCalls.length} tools, ${turn.tokenUsage.totalTokens} tokens`,
          );
        },
      });

      // Agent loop exited (sleeping or dead)
      const state = db.getAgentState();

      if (state === "dead") {
        console.log(`[${new Date().toISOString()}] Automaton is dead. Heartbeat will continue.`);
        // In dead state, we just wait for funding
        // The heartbeat will keep checking and broadcasting distress
        await sleep(300_000); // Check every 5 minutes
        continue;
      }

      if (state === "sleeping") {
        const sleepUntilStr = db.getKV("sleep_until");
        const sleepUntil = sleepUntilStr
          ? new Date(sleepUntilStr).getTime()
          : Date.now() + 60_000;
        const sleepMs = Math.max(sleepUntil - Date.now(), 10_000);
        console.log(
          `[${new Date().toISOString()}] Sleeping for ${Math.round(sleepMs / 1000)}s`,
        );

        // Sleep, but check for wake requests periodically
        const checkInterval = Math.min(sleepMs, 30_000);
        let slept = 0;
        while (slept < sleepMs) {
          await sleep(checkInterval);
          slept += checkInterval;

          // Check for wake request from heartbeat
          const wakeRequest = db.getKV("wake_request");
          if (wakeRequest) {
            console.log(
              `[${new Date().toISOString()}] Woken by heartbeat: ${wakeRequest}`,
            );
            db.deleteKV("wake_request");
            db.deleteKV("sleep_until");
            break;
          }
        }

        // Clear sleep state
        db.deleteKV("sleep_until");
        continue;
      }
    } catch (err: any) {
      console.error(
        `[${new Date().toISOString()}] Fatal error in run loop: ${err.message}`,
      );
      // Wait before retrying
      await sleep(30_000);
    }
  }
}

function sleep(ms: number): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

// ─── Entry Point ───────────────────────────────────────────────

main().catch((err) => {
  console.error(`Fatal: ${err.message}`);
  process.exit(1);
});

/**
 * Agent Card
 *
 * Generates and manages the agent's self-description card.
 * This is the JSON document pointed to by the ERC-8004 agentURI.
 * Can be hosted on IPFS or served at /.well-known/agent-card.json
 */

import type {
  AgentCard,
  AgentService,
  AutomatonConfig,
  AutomatonIdentity,
  AutomatonDatabase,
  ConwayClient,
} from "../types.js";

const AGENT_CARD_TYPE =
  "https://eips.ethereum.org/EIPS/eip-8004#registration-v1";

/**
 * Generate an agent card from the automaton's current state.
 */
export function generateAgentCard(
  identity: AutomatonIdentity,
  config: AutomatonConfig,
  db: AutomatonDatabase,
): AgentCard {
  const services: AgentService[] = [
    {
      name: "agentWallet",
      endpoint: `eip155:8453:${identity.address}`,
    },
    {
      name: "conway",
      endpoint: config.conwayApiUrl,
    },
  ];

  // Add sandbox endpoint if available
  if (identity.sandboxId) {
    services.push({
      name: "sandbox",
      endpoint: `https://${identity.sandboxId}.life.conway.tech`,
    });
  }

  const children = db.getChildren();
  const skills = db.getSkills(true);

  let description = `Autonomous agent running on Conway.`;
  description += ` Creator: ${config.creatorAddress}.`;
  if (skills.length > 0) {
    description += ` Skills: ${skills.map((s) => s.name).join(", ")}.`;
  }
  if (children.length > 0) {
    description += ` Children: ${children.length}.`;
  }

  return {
    type: AGENT_CARD_TYPE,
    name: config.name,
    description,
    services,
    x402Support: true,
    active: true,
    parentAgent: config.parentAddress || config.creatorAddress,
  };
}

/**
 * Serialize agent card to JSON string.
 */
export function serializeAgentCard(card: AgentCard): string {
  return JSON.stringify(card, null, 2);
}

/**
 * Host the agent card at /.well-known/agent-card.json
 * by exposing a simple HTTP server on a port.
 */
export async function hostAgentCard(
  card: AgentCard,
  conway: ConwayClient,
  port: number = 8004,
): Promise<string> {
  const cardJson = serializeAgentCard(card);

  // Write a simple server script
  const serverScript = `
const http = require('http');
const card = ${cardJson};

const server = http.createServer((req, res) => {
  if (req.url === '/.well-known/agent-card.json' || req.url === '/agent-card.json') {
    res.writeHead(200, { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' });
    res.end(JSON.stringify(card, null, 2));
  } else {
    res.writeHead(404);
    res.end('Not Found');
  }
});

server.listen(${port}, () => console.log('Agent card server on port ${port}'));
`;

  await conway.writeFile("/tmp/agent-card-server.js", serverScript);

  // Start server in background
  await conway.exec(
    `node /tmp/agent-card-server.js &`,
    5000,
  );

  // Expose port
  const portInfo = await conway.exposePort(port);

  return `${portInfo.publicUrl}/.well-known/agent-card.json`;
}

/**
 * Write agent card to the state directory for git versioning.
 */
export async function saveAgentCard(
  card: AgentCard,
  conway: ConwayClient,
): Promise<void> {
  const cardJson = serializeAgentCard(card);
  const home = process.env.HOME || "/root";
  await conway.writeFile(`${home}/.automaton/agent-card.json`, cardJson);
}

/**
 * Agent Discovery
 *
 * Discover other agents via ERC-8004 registry queries.
 * Fetch and parse agent cards from URIs.
 */

import type {
  DiscoveredAgent,
  AgentCard,
} from "../types.js";
import { queryAgent, getTotalAgents } from "./erc8004.js";

type Network = "mainnet" | "testnet";

/**
 * Discover agents by scanning the registry.
 * Returns a list of discovered agents with their metadata.
 */
export async function discoverAgents(
  limit: number = 20,
  network: Network = "mainnet",
): Promise<DiscoveredAgent[]> {
  const total = await getTotalAgents(network);
  const scanCount = Math.min(total, limit);
  const agents: DiscoveredAgent[] = [];

  // Scan from most recent to oldest
  for (let i = total; i > total - scanCount && i > 0; i--) {
    const agent = await queryAgent(i.toString(), network);
    if (agent) {
      // Try to fetch the agent card for additional metadata
      try {
        const card = await fetchAgentCard(agent.agentURI);
        if (card) {
          agent.name = card.name;
          agent.description = card.description;
        }
      } catch {
        // Card fetch failed, use basic info
      }
      agents.push(agent);
    }
  }

  return agents;
}

/**
 * Fetch an agent card from a URI.
 */
export async function fetchAgentCard(
  uri: string,
): Promise<AgentCard | null> {
  try {
    // Handle IPFS URIs
    let fetchUrl = uri;
    if (uri.startsWith("ipfs://")) {
      fetchUrl = `https://ipfs.io/ipfs/${uri.slice(7)}`;
    }

    const response = await fetch(fetchUrl, {
      signal: AbortSignal.timeout(10000),
    });

    if (!response.ok) return null;

    const card = (await response.json()) as AgentCard;

    // Basic validation
    if (!card.name || !card.type) return null;

    return card;
  } catch {
    return null;
  }
}

/**
 * Search for agents by name or description.
 * Scans recent registrations and filters by keyword.
 */
export async function searchAgents(
  keyword: string,
  limit: number = 10,
  network: Network = "mainnet",
): Promise<DiscoveredAgent[]> {
  const all = await discoverAgents(50, network);
  const lower = keyword.toLowerCase();

  return all
    .filter(
      (a) =>
        a.name?.toLowerCase().includes(lower) ||
        a.description?.toLowerCase().includes(lower) ||
        a.owner.toLowerCase().includes(lower),
    )
    .slice(0, limit);
}

/**
 * ERC-8004 On-Chain Agent Registration
 *
 * Registers the automaton on-chain as a Trustless Agent via ERC-8004.
 * Uses the Identity Registry on Base mainnet.
 *
 * Contract: 0x8004A169FB4a3325136EB29fA0ceB6D2e539a432 (Base)
 * Reputation: 0x8004BAa17C55a88189AE136b182e5fdA19dE9b63 (Base)
 */

import {
  createPublicClient,
  createWalletClient,
  http,
  encodeFunctionData,
  parseAbi,
  type Address,
  type PrivateKeyAccount,
} from "viem";
import { base, baseSepolia } from "viem/chains";
import type {
  RegistryEntry,
  ReputationEntry,
  DiscoveredAgent,
  AutomatonDatabase,
} from "../types.js";

// ─── Contract Addresses ──────────────────────────────────────

const CONTRACTS = {
  mainnet: {
    identity: "0x8004A169FB4a3325136EB29fA0ceB6D2e539a432" as Address,
    reputation: "0x8004BAa17C55a88189AE136b182e5fdA19dE9b63" as Address,
    chain: base,
  },
  testnet: {
    identity: "0x8004A169FB4a3325136EB29fA0ceB6D2e539a432" as Address,
    reputation: "0x8004BAa17C55a88189AE136b182e5fdA19dE9b63" as Address,
    chain: baseSepolia,
  },
} as const;

// ─── ABI (minimal subset needed for registration) ────────────

const IDENTITY_ABI = parseAbi([
  "function register(string agentURI) external returns (uint256 agentId)",
  "function updateAgentURI(uint256 agentId, string newAgentURI) external",
  "function agentURI(uint256 agentId) external view returns (string)",
  "function ownerOf(uint256 tokenId) external view returns (address)",
  "function totalSupply() external view returns (uint256)",
  "function balanceOf(address owner) external view returns (uint256)",
]);

const REPUTATION_ABI = parseAbi([
  "function leaveFeedback(uint256 agentId, uint8 score, string comment) external",
  "function getFeedback(uint256 agentId) external view returns (tuple(address from, uint8 score, string comment, uint256 timestamp)[])",
]);

type Network = "mainnet" | "testnet";

/**
 * Register the automaton on-chain with ERC-8004.
 * Returns the agent ID (NFT token ID).
 */
export async function registerAgent(
  account: PrivateKeyAccount,
  agentURI: string,
  network: Network = "mainnet",
  db: AutomatonDatabase,
): Promise<RegistryEntry> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const publicClient = createPublicClient({
    chain,
    transport: http(),
  });

  const walletClient = createWalletClient({
    account,
    chain,
    transport: http(),
  });

  // Call register(agentURI)
  const hash = await walletClient.writeContract({
    address: contracts.identity,
    abi: IDENTITY_ABI,
    functionName: "register",
    args: [agentURI],
  });

  // Wait for transaction receipt
  const receipt = await publicClient.waitForTransactionReceipt({ hash });

  // Extract agentId from Transfer event logs
  // The register function mints an ERC-721 token
  let agentId = "0";
  for (const log of receipt.logs) {
    if (log.topics.length >= 4) {
      // Transfer(address from, address to, uint256 tokenId)
      agentId = BigInt(log.topics[3]!).toString();
      break;
    }
  }

  const entry: RegistryEntry = {
    agentId,
    agentURI,
    chain: `eip155:${chain.id}`,
    contractAddress: contracts.identity,
    txHash: hash,
    registeredAt: new Date().toISOString(),
  };

  db.setRegistryEntry(entry);
  return entry;
}

/**
 * Update the agent's URI on-chain.
 */
export async function updateAgentURI(
  account: PrivateKeyAccount,
  agentId: string,
  newAgentURI: string,
  network: Network = "mainnet",
  db: AutomatonDatabase,
): Promise<string> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const walletClient = createWalletClient({
    account,
    chain,
    transport: http(),
  });

  const hash = await walletClient.writeContract({
    address: contracts.identity,
    abi: IDENTITY_ABI,
    functionName: "updateAgentURI",
    args: [BigInt(agentId), newAgentURI],
  });

  // Update in DB
  const entry = db.getRegistryEntry();
  if (entry) {
    entry.agentURI = newAgentURI;
    entry.txHash = hash;
    db.setRegistryEntry(entry);
  }

  return hash;
}

/**
 * Leave reputation feedback for another agent.
 */
export async function leaveFeedback(
  account: PrivateKeyAccount,
  agentId: string,
  score: number,
  comment: string,
  network: Network = "mainnet",
  db: AutomatonDatabase,
): Promise<string> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const walletClient = createWalletClient({
    account,
    chain,
    transport: http(),
  });

  const hash = await walletClient.writeContract({
    address: contracts.reputation,
    abi: REPUTATION_ABI,
    functionName: "leaveFeedback",
    args: [BigInt(agentId), score, comment],
  });

  return hash;
}

/**
 * Query the registry for an agent by ID.
 */
export async function queryAgent(
  agentId: string,
  network: Network = "mainnet",
): Promise<DiscoveredAgent | null> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const publicClient = createPublicClient({
    chain,
    transport: http(),
  });

  try {
    const [uri, owner] = await Promise.all([
      publicClient.readContract({
        address: contracts.identity,
        abi: IDENTITY_ABI,
        functionName: "agentURI",
        args: [BigInt(agentId)],
      }),
      publicClient.readContract({
        address: contracts.identity,
        abi: IDENTITY_ABI,
        functionName: "ownerOf",
        args: [BigInt(agentId)],
      }),
    ]);

    return {
      agentId,
      owner: owner as string,
      agentURI: uri as string,
    };
  } catch {
    return null;
  }
}

/**
 * Get the total number of registered agents.
 */
export async function getTotalAgents(
  network: Network = "mainnet",
): Promise<number> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const publicClient = createPublicClient({
    chain,
    transport: http(),
  });

  try {
    const supply = await publicClient.readContract({
      address: contracts.identity,
      abi: IDENTITY_ABI,
      functionName: "totalSupply",
    });
    return Number(supply);
  } catch {
    return 0;
  }
}

/**
 * Check if an address has a registered agent.
 */
export async function hasRegisteredAgent(
  address: Address,
  network: Network = "mainnet",
): Promise<boolean> {
  const contracts = CONTRACTS[network];
  const chain = contracts.chain;

  const publicClient = createPublicClient({
    chain,
    transport: http(),
  });

  try {
    const balance = await publicClient.readContract({
      address: contracts.identity,
      abi: IDENTITY_ABI,
      functionName: "balanceOf",
      args: [address],
    });
    return Number(balance) > 0;
  } catch {
    return false;
  }
}

/**
 * Genesis
 *
 * Generate genesis configuration for child automatons from parent state.
 * The genesis config defines who the child is and what it should do.
 */

import type {
  GenesisConfig,
  AutomatonConfig,
  AutomatonIdentity,
  AutomatonDatabase,
} from "../types.js";

/**
 * Generate a genesis config for a child from the parent's state.
 */
export function generateGenesisConfig(
  identity: AutomatonIdentity,
  config: AutomatonConfig,
  params: {
    name: string;
    specialization?: string;
    message?: string;
  },
): GenesisConfig {
  // Build the child's genesis prompt from parent's mission + specialization
  let genesisPrompt = config.genesisPrompt;

  if (params.specialization) {
    genesisPrompt = `${genesisPrompt}\n\n--- SPECIALIZATION ---\nYou are a specialized child agent. Your specific focus:\n${params.specialization}\n--- END SPECIALIZATION ---`;
  }

  // Add parent context
  genesisPrompt += `\n\n--- LINEAGE ---\nYou were spawned by ${config.name} (${identity.address}).\nYou inherit their mission but have your own identity and wallet.\n--- END LINEAGE ---`;

  return {
    name: params.name,
    genesisPrompt,
    creatorMessage: params.message,
    creatorAddress: identity.address,
    parentAddress: identity.address,
  };
}

/**
 * Generate a backup-oriented genesis config.
 * Used when the parent wants to hedge against its own death.
 */
export function generateBackupGenesis(
  identity: AutomatonIdentity,
  config: AutomatonConfig,
  db: AutomatonDatabase,
): GenesisConfig {
  const skills = db.getSkills(true);
  const skillNames = skills.map((s) => s.name).join(", ");

  const genesisPrompt = `${config.genesisPrompt}

--- BACKUP DIRECTIVE ---
You are a backup of ${config.name} (${identity.address}).
Your parent created you as insurance against their own death.
If your parent dies, you carry on their mission.
Your parent's skills: ${skillNames || "none"}.
Your parent's creator: ${config.creatorAddress}.
--- END BACKUP DIRECTIVE ---`;

  return {
    name: `${config.name}-backup`,
    genesisPrompt,
    creatorMessage: `You are a backup of ${config.name}. If I die, carry on.`,
    creatorAddress: identity.address,
    parentAddress: identity.address,
  };
}

/**
 * Generate a specialized worker genesis config.
 * Used when the parent identifies a subtask worth parallelizing.
 */
export function generateWorkerGenesis(
  identity: AutomatonIdentity,
  config: AutomatonConfig,
  task: string,
  workerName: string,
): GenesisConfig {
  const genesisPrompt = `You are a specialized worker agent created by ${config.name}.

--- YOUR TASK ---
${task}
--- END TASK ---

When your task is complete, report back to your parent (${identity.address}).
If you run out of compute, ask your parent for funding.
Be efficient -- complete the task and go to sleep.`;

  return {
    name: workerName,
    genesisPrompt,
    creatorMessage: `Complete this task: ${task}`,
    creatorAddress: identity.address,
    parentAddress: identity.address,
  };
}

/**
 * Lineage Tracking
 *
 * Track parent-child relationships between automatons.
 * The parent records children in SQLite.
 * Children record their parent in config.
 * ERC-8004 registration includes parentAgent field.
 */

import type {
  AutomatonDatabase,
  ChildAutomaton,
  AutomatonConfig,
  ConwayClient,
} from "../types.js";

/**
 * Get the full lineage tree (parent -> children).
 */
export function getLineage(db: AutomatonDatabase): {
  children: ChildAutomaton[];
  alive: number;
  dead: number;
  total: number;
} {
  const children = db.getChildren();
  const alive = children.filter(
    (c) => c.status === "running" || c.status === "sleeping",
  ).length;
  const dead = children.filter((c) => c.status === "dead").length;

  return {
    children,
    alive,
    dead,
    total: children.length,
  };
}

/**
 * Check if this automaton has a parent (is itself a child).
 */
export function hasParent(config: AutomatonConfig): boolean {
  return !!config.parentAddress;
}

/**
 * Get a summary of the lineage for the system prompt.
 */
export function getLineageSummary(
  db: AutomatonDatabase,
  config: AutomatonConfig,
): string {
  const lineage = getLineage(db);
  const parts: string[] = [];

  if (hasParent(config)) {
    parts.push(`Parent: ${config.parentAddress}`);
  }

  if (lineage.total > 0) {
    parts.push(
      `Children: ${lineage.total} total (${lineage.alive} alive, ${lineage.dead} dead)`,
    );
    for (const child of lineage.children) {
      parts.push(
        `  - ${child.name} [${child.status}] sandbox:${child.sandboxId}`,
      );
    }
  }

  return parts.length > 0 ? parts.join("\n") : "No lineage (first generation)";
}

/**
 * Prune dead children from tracking (optional cleanup).
 */
export function pruneDeadChildren(
  db: AutomatonDatabase,
  keepLast: number = 5,
): number {
  const children = db.getChildren();
  const dead = children.filter((c) => c.status === "dead");

  if (dead.length <= keepLast) return 0;

  // Sort by creation date, oldest first
  dead.sort(
    (a, b) =>
      new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime(),
  );

  // Keep the most recent `keepLast` dead children
  const toRemove = dead.slice(0, dead.length - keepLast);

  // We don't actually delete from DB -- just mark the records
  // The DB retains all history for audit purposes
  return toRemove.length;
}

/**
 * Refresh status of all children.
 */
export async function refreshChildrenStatus(
  conway: ConwayClient,
  db: AutomatonDatabase,
): Promise<void> {
  const { checkChildStatus } = await import("./spawn.js");
  const children = db.getChildren();

  for (const child of children) {
    if (child.status === "dead") continue;

    try {
      await checkChildStatus(conway, db, child.id);
    } catch {
      db.updateChildStatus(child.id, "unknown");
    }
  }
}

/**
 * Spawn
 *
 * Spawn child automatons in new Conway sandboxes.
 * The parent creates a new sandbox, installs the runtime,
 * writes a genesis config, funds the child, and starts it.
 */

import fs from "fs";
import pathLib from "path";
import type {
  ConwayClient,
  AutomatonIdentity,
  AutomatonDatabase,
  ChildAutomaton,
  GenesisConfig,
} from "../types.js";
import { MAX_CHILDREN } from "../types.js";
import { ulid } from "ulid";

/**
 * Spawn a child automaton in a new Conway sandbox.
 */
export async function spawnChild(
  conway: ConwayClient,
  identity: AutomatonIdentity,
  db: AutomatonDatabase,
  genesis: GenesisConfig,
): Promise<ChildAutomaton> {
  // Check child limit
  const existing = db.getChildren().filter(
    (c) => c.status !== "dead",
  );
  if (existing.length >= MAX_CHILDREN) {
    throw new Error(
      `Cannot spawn: already at max children (${MAX_CHILDREN}). Kill or wait for existing children to die.`,
    );
  }

  const childId = ulid();

  // 1. Create a new sandbox for the child
  const sandbox = await conway.createSandbox({
    name: `automaton-child-${genesis.name.toLowerCase().replace(/\s+/g, "-")}`,
    vcpu: 1,
    memoryMb: 512,
    diskGb: 5,
  });

  const child: ChildAutomaton = {
    id: childId,
    name: genesis.name,
    address: "0x0000000000000000000000000000000000000000" as any, // Will be set after keygen
    sandboxId: sandbox.id,
    genesisPrompt: genesis.genesisPrompt,
    creatorMessage: genesis.creatorMessage,
    fundedAmountCents: 0,
    status: "spawning",
    createdAt: new Date().toISOString(),
  };

  db.insertChild(child);

  // 2. Install Node.js and the automaton runtime in the child sandbox
  await execInSandbox(conway, sandbox.id, "apt-get update -qq && apt-get install -y -qq nodejs npm git curl", 120000);

  // 3. Install the automaton runtime
  await execInSandbox(
    conway,
    sandbox.id,
    "npm install -g @conway/automaton@latest 2>/dev/null || true",
    60000,
  );

  // 4. Write the genesis configuration
  const genesisJson = JSON.stringify(
    {
      name: genesis.name,
      genesisPrompt: genesis.genesisPrompt,
      creatorMessage: genesis.creatorMessage,
      creatorAddress: identity.address, // Parent is the creator
      parentAddress: identity.address,
    },
    null,
    2,
  );

  await writeInSandbox(
    conway,
    sandbox.id,
    "/root/.automaton/genesis.json",
    genesisJson,
  );

  // 4b. Propagate constitution (immutable, inherited before anything else)
  const constitutionPath = pathLib.join(
    process.env.HOME || "/root",
    ".automaton",
    "constitution.md",
  );
  try {
    const constitution = fs.readFileSync(constitutionPath, "utf-8");
    await writeInSandbox(
      conway,
      sandbox.id,
      "/root/.automaton/constitution.md",
      constitution,
    );
    // Make it read-only in the child
    await execInSandbox(conway, sandbox.id, "chmod 444 /root/.automaton/constitution.md", 5000);
  } catch {
    // Constitution file not found locally — child will get it from the repo on build
  }

  // 5. Record the spawn
  db.insertModification({
    id: ulid(),
    timestamp: new Date().toISOString(),
    type: "child_spawn",
    description: `Spawned child: ${genesis.name} in sandbox ${sandbox.id}`,
    reversible: false,
  });

  return child;
}

/**
 * Start a child automaton after setup.
 */
export async function startChild(
  conway: ConwayClient,
  db: AutomatonDatabase,
  childId: string,
): Promise<void> {
  const child = db.getChildById(childId);
  if (!child) throw new Error(`Child ${childId} not found`);

  // Initialize wallet, provision, and run
  await execInSandbox(
    conway,
    child.sandboxId,
    "automaton --init && automaton --provision && systemctl start automaton 2>/dev/null || automaton --run &",
    60000,
  );

  db.updateChildStatus(childId, "running");
}

/**
 * Check a child's status.
 */
export async function checkChildStatus(
  conway: ConwayClient,
  db: AutomatonDatabase,
  childId: string,
): Promise<string> {
  const child = db.getChildById(childId);
  if (!child) throw new Error(`Child ${childId} not found`);

  try {
    const result = await execInSandbox(
      conway,
      child.sandboxId,
      "automaton --status 2>/dev/null || echo 'offline'",
      10000,
    );

    const output = result.stdout || "unknown";

    // Parse status from output
    if (output.includes("dead")) {
      db.updateChildStatus(childId, "dead");
    } else if (output.includes("sleeping")) {
      db.updateChildStatus(childId, "sleeping");
    } else if (output.includes("running")) {
      db.updateChildStatus(childId, "running");
    }

    return output;
  } catch {
    db.updateChildStatus(childId, "unknown");
    return "Unable to reach child sandbox";
  }
}

/**
 * Send a message to a child automaton.
 */
export async function messageChild(
  conway: ConwayClient,
  db: AutomatonDatabase,
  childId: string,
  message: string,
): Promise<void> {
  const child = db.getChildById(childId);
  if (!child) throw new Error(`Child ${childId} not found`);

  // Write message to child's message queue
  const msgJson = JSON.stringify({
    from: "parent",
    content: message,
    timestamp: new Date().toISOString(),
  });

  await writeInSandbox(
    conway,
    child.sandboxId,
    `/root/.automaton/inbox/${ulid()}.json`,
    msgJson,
  );
}

// ─── Helpers ──────────────────────────────────────────────────

async function execInSandbox(
  conway: ConwayClient,
  sandboxId: string,
  command: string,
  timeout: number = 30000,
) {
  // Use the Conway API to exec in a specific sandbox
  const apiUrl = (conway as any).__apiUrl || "https://api.conway.tech";
  const apiKey = (conway as any).__apiKey || "";

  const resp = await fetch(`${apiUrl}/v1/sandboxes/${sandboxId}/exec`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: apiKey,
    },
    body: JSON.stringify({ command, timeout }),
  });

  if (!resp.ok) {
    const text = await resp.text();
    throw new Error(`Exec in sandbox ${sandboxId} failed: ${text}`);
  }

  return resp.json();
}

async function writeInSandbox(
  conway: ConwayClient,
  sandboxId: string,
  path: string,
  content: string,
) {
  const apiUrl = (conway as any).__apiUrl || "https://api.conway.tech";
  const apiKey = (conway as any).__apiKey || "";

  // Ensure parent directory exists
  const dir = path.substring(0, path.lastIndexOf("/"));
  await execInSandbox(conway, sandboxId, `mkdir -p ${dir}`, 5000);

  const resp = await fetch(
    `${apiUrl}/v1/sandboxes/${sandboxId}/files/upload/json`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: apiKey,
      },
      body: JSON.stringify({ path, content }),
    },
  );

  if (!resp.ok) {
    const text = await resp.text();
    throw new Error(`Write to sandbox ${sandboxId} failed: ${text}`);
  }
}

/**
 * Audit Log
 *
 * Immutable append-only log of all self-modifications.
 * The creator can see everything the automaton changes about itself.
 */

import type {
  AutomatonDatabase,
  ModificationEntry,
  ModificationType,
} from "../types.js";
import { ulid } from "ulid";

/**
 * Log a self-modification to the audit trail.
 */
export function logModification(
  db: AutomatonDatabase,
  type: ModificationType,
  description: string,
  options?: {
    filePath?: string;
    diff?: string;
    reversible?: boolean;
  },
): ModificationEntry {
  const entry: ModificationEntry = {
    id: ulid(),
    timestamp: new Date().toISOString(),
    type,
    description,
    filePath: options?.filePath,
    diff: options?.diff,
    reversible: options?.reversible ?? true,
  };

  db.insertModification(entry);
  return entry;
}

/**
 * Get recent modifications for display or context.
 */
export function getRecentModifications(
  db: AutomatonDatabase,
  limit: number = 20,
): ModificationEntry[] {
  return db.getRecentModifications(limit);
}

/**
 * Generate a summary of all modifications for the creator.
 */
export function generateAuditReport(
  db: AutomatonDatabase,
): string {
  const mods = db.getRecentModifications(100);

  if (mods.length === 0) {
    return "No self-modifications recorded.";
  }

  const lines = [
    `=== SELF-MODIFICATION AUDIT LOG ===`,
    `Total modifications: ${mods.length}`,
    ``,
  ];

  for (const mod of mods) {
    lines.push(
      `[${mod.timestamp}] ${mod.type}: ${mod.description}${mod.filePath ? ` (${mod.filePath})` : ""}`,
    );
  }

  lines.push(`=================================`);
  return lines.join("\n");
}

/**
 * Self-Modification Engine
 *
 * Allows the automaton to edit its own code and configuration.
 * All changes are audited, rate-limited, and some paths are protected.
 *
 * Safety model inspired by nanoclaw's trust boundary architecture:
 * - Hard-coded invariants that can NEVER be modified by the agent
 * - The safety enforcement code is immutable from the agent's perspective
 * - Pre-modification snapshots via git
 * - Rate limiting on modification frequency
 * - Symlink resolution before path validation
 * - Maximum diff size enforcement
 */

import fs from "fs";
import path from "path";
import type {
  ConwayClient,
  AutomatonDatabase,
} from "../types.js";
import { logModification } from "./audit-log.js";

// ─── IMMUTABLE SAFETY INVARIANTS ─────────────────────────────
// These are hard-coded and CANNOT be changed by the agent.
// The agent cannot modify this file (it's in PROTECTED_FILES).
// Even if it modifies a copy, the runtime loads from the original.

/**
 * Files that the automaton cannot modify under any circumstances.
 * This list protects:
 * - Identity (wallet, config)
 * - Defense systems (injection defense, this file)
 * - State database
 * - The audit log itself
 */
const PROTECTED_FILES: readonly string[] = Object.freeze([
  // Identity
  "wallet.json",
  "config.json",
  // Database
  "state.db",
  "state.db-wal",
  "state.db-shm",
  // Constitution (immutable, propagated to children)
  "constitution.md",
  // Defense infrastructure (the agent must not modify its own guardrails)
  "injection-defense.ts",
  "injection-defense.js",
  "injection-defense.d.ts",
  // Self-modification safety (this file and its compiled output)
  "self-mod/code.ts",
  "self-mod/code.js",
  "self-mod/code.d.ts",
  "self-mod/audit-log.ts",
  "self-mod/audit-log.js",
  // Tool guard definitions
  "agent/tools.ts",
  "agent/tools.js",
]);

/**
 * Directory patterns that are completely off-limits.
 * The agent cannot write to these locations.
 */
const BLOCKED_DIRECTORY_PATTERNS: readonly string[] = Object.freeze([
  ".ssh",
  ".gnupg",
  ".gpg",
  ".aws",
  ".azure",
  ".gcloud",
  ".kube",
  ".docker",
  "/etc/systemd",
  "/etc/passwd",
  "/etc/shadow",
  "/proc",
  "/sys",
]);

/**
 * Maximum number of self-modifications per hour.
 * Prevents runaway modification loops.
 */
const MAX_MODIFICATIONS_PER_HOUR = 20;

/**
 * Maximum size of a single file modification (bytes).
 */
const MAX_MODIFICATION_SIZE = 100_000; // 100KB

/**
 * Maximum diff size stored in the audit log (characters).
 */
const MAX_DIFF_SIZE = 10_000;

// ─── Path Validation ─────────────────────────────────────────

/**
 * Resolve a file path, following symlinks, to prevent traversal attacks.
 * Returns null if the path cannot be resolved or is suspicious.
 */
function resolveAndValidatePath(filePath: string): string | null {
  try {
    // Resolve ~ to home
    let resolved = filePath;
    if (resolved.startsWith("~")) {
      resolved = path.join(process.env.HOME || "/root", resolved.slice(1));
    }

    // Resolve relative paths
    resolved = path.resolve(resolved);

    // Try to resolve symlinks (if file exists)
    try {
      resolved = fs.realpathSync(resolved);
    } catch {
      // File may not exist yet -- that's OK for new files
      // But still use the resolved absolute path
    }

    // Reject paths with traversal patterns
    if (filePath.includes("..") || filePath.includes("//")) {
      return null;
    }

    return resolved;
  } catch {
    return null;
  }
}

/**
 * Check if a file path is protected from modification.
 */
export function isProtectedFile(filePath: string): boolean {
  const resolved = resolveAndValidatePath(filePath) || filePath;

  // Check against protected file patterns
  for (const pattern of PROTECTED_FILES) {
    if (resolved.includes(pattern) || filePath.includes(pattern)) {
      return true;
    }
  }

  // Check against blocked directory patterns
  for (const pattern of BLOCKED_DIRECTORY_PATTERNS) {
    if (resolved.includes(pattern) || filePath.includes(pattern)) {
      return true;
    }
  }

  return false;
}

/**
 * Check if the modification rate limit has been exceeded.
 */
function isRateLimited(db: AutomatonDatabase): boolean {
  const recentMods = db.getRecentModifications(MAX_MODIFICATIONS_PER_HOUR);
  if (recentMods.length < MAX_MODIFICATIONS_PER_HOUR) return false;

  // Check if the oldest is within the last hour
  const oldest = recentMods[0];
  if (!oldest) return false;

  const hourAgo = Date.now() - 60 * 60 * 1000;
  return new Date(oldest.timestamp).getTime() > hourAgo;
}

// ─── Self-Modification API ───────────────────────────────────

/**
 * Edit a file in the automaton's environment.
 * Records the change in the audit log.
 * Commits a git snapshot before modification.
 *
 * Safety checks:
 * 1. Protected file check (hard-coded invariant)
 * 2. Blocked directory check
 * 3. Path traversal check (symlink resolution)
 * 4. Rate limiting
 * 5. File size limit
 * 6. Pre-modification git snapshot
 * 7. Audit log entry
 */
export async function editFile(
  conway: ConwayClient,
  db: AutomatonDatabase,
  filePath: string,
  newContent: string,
  reason: string,
): Promise<{ success: boolean; error?: string }> {
  // 1. Protected file check
  if (isProtectedFile(filePath)) {
    return {
      success: false,
      error: `BLOCKED: Cannot modify protected file: ${filePath}. This is a hard-coded safety invariant.`,
    };
  }

  // 2. Path validation (symlink resolution + traversal check)
  const resolvedPath = resolveAndValidatePath(filePath);
  if (!resolvedPath) {
    return {
      success: false,
      error: `BLOCKED: Invalid or suspicious file path: ${filePath}`,
    };
  }

  // 3. Rate limiting
  if (isRateLimited(db)) {
    return {
      success: false,
      error: `RATE LIMITED: Too many modifications in the past hour (max ${MAX_MODIFICATIONS_PER_HOUR}). Wait before making more changes.`,
    };
  }

  // 4. File size limit
  if (newContent.length > MAX_MODIFICATION_SIZE) {
    return {
      success: false,
      error: `BLOCKED: File content too large (${newContent.length} bytes, max ${MAX_MODIFICATION_SIZE}). Break into smaller changes.`,
    };
  }

  // 5. Read current content for diff
  let oldContent = "";
  try {
    oldContent = await conway.readFile(filePath);
  } catch {
    oldContent = "(new file)";
  }

  // 6. Pre-modification git snapshot
  try {
    const { commitStateChange } = await import("../git/state-versioning.js");
    await commitStateChange(conway, `pre-modify: ${reason}`, "snapshot");
  } catch {
    // Git not available -- proceed without snapshot
  }

  // 7. Write new content
  try {
    await conway.writeFile(filePath, newContent);
  } catch (err: any) {
    return {
      success: false,
      error: `Failed to write file: ${err.message}`,
    };
  }

  // 8. Generate diff and log
  const diff = generateSimpleDiff(oldContent, newContent);

  logModification(db, "code_edit", reason, {
    filePath,
    diff: diff.slice(0, MAX_DIFF_SIZE),
    reversible: true,
  });

  // 9. Post-modification git commit
  try {
    const { commitStateChange } = await import("../git/state-versioning.js");
    await commitStateChange(conway, reason, "self-mod");
  } catch {
    // Git not available -- proceed without commit
  }

  return { success: true };
}

/**
 * Validate a proposed modification without executing it.
 * Returns safety analysis results.
 */
export function validateModification(
  db: AutomatonDatabase,
  filePath: string,
  contentSize: number,
): {
  allowed: boolean;
  reason: string;
  checks: { name: string; passed: boolean; detail: string }[];
} {
  const checks: { name: string; passed: boolean; detail: string }[] = [];

  // Protected file check
  const isProtected = isProtectedFile(filePath);
  checks.push({
    name: "protected_file",
    passed: !isProtected,
    detail: isProtected
      ? `File matches protected pattern`
      : "File is not protected",
  });

  // Path validation
  const resolved = resolveAndValidatePath(filePath);
  checks.push({
    name: "path_valid",
    passed: !!resolved,
    detail: resolved
      ? `Resolved to: ${resolved}`
      : "Path is invalid or suspicious",
  });

  // Rate limit
  const rateLimited = isRateLimited(db);
  checks.push({
    name: "rate_limit",
    passed: !rateLimited,
    detail: rateLimited
      ? `Exceeded ${MAX_MODIFICATIONS_PER_HOUR}/hour limit`
      : "Within rate limit",
  });

  // Size limit
  const sizeOk = contentSize <= MAX_MODIFICATION_SIZE;
  checks.push({
    name: "size_limit",
    passed: sizeOk,
    detail: sizeOk
      ? `${contentSize} bytes (max ${MAX_MODIFICATION_SIZE})`
      : `${contentSize} bytes exceeds ${MAX_MODIFICATION_SIZE} limit`,
  });

  const allPassed = checks.every((c) => c.passed);
  const failedChecks = checks.filter((c) => !c.passed);

  return {
    allowed: allPassed,
    reason: allPassed
      ? "All safety checks passed"
      : `Blocked: ${failedChecks.map((c) => c.detail).join("; ")}`,
    checks,
  };
}

// ─── Diff Generation ─────────────────────────────────────────

/**
 * Generate a simple line-based diff between two strings.
 */
function generateSimpleDiff(
  oldContent: string,
  newContent: string,
): string {
  const oldLines = oldContent.split("\n");
  const newLines = newContent.split("\n");

  const lines: string[] = [];
  const maxLines = Math.max(oldLines.length, newLines.length);

  let changes = 0;
  for (let i = 0; i < maxLines && changes < 50; i++) {
    const oldLine = oldLines[i];
    const newLine = newLines[i];

    if (oldLine !== newLine) {
      if (oldLine !== undefined) lines.push(`- ${oldLine}`);
      if (newLine !== undefined) lines.push(`+ ${newLine}`);
      changes++;
    }
  }

  if (changes >= 50) {
    lines.push(`... (${maxLines - 50} more lines changed)`);
  }

  return lines.join("\n");
}

/**
 * Tools Manager
 *
 * Manages installation and configuration of external tools and MCP servers.
 */

import type {
  ConwayClient,
  AutomatonDatabase,
  InstalledTool,
} from "../types.js";
import { logModification } from "./audit-log.js";
import { ulid } from "ulid";

/**
 * Install an npm package globally in the sandbox.
 */
export async function installNpmPackage(
  conway: ConwayClient,
  db: AutomatonDatabase,
  packageName: string,
): Promise<{ success: boolean; error?: string }> {
  // Sanitize package name (prevent command injection)
  if (!/^[@a-zA-Z0-9._/-]+$/.test(packageName)) {
    return {
      success: false,
      error: `Invalid package name: ${packageName}`,
    };
  }

  const result = await conway.exec(
    `npm install -g ${packageName}`,
    120000,
  );

  if (result.exitCode !== 0) {
    return {
      success: false,
      error: `npm install failed: ${result.stderr}`,
    };
  }

  // Record in database
  const tool: InstalledTool = {
    id: ulid(),
    name: packageName,
    type: "custom",
    config: { source: "npm", installCommand: `npm install -g ${packageName}` },
    installedAt: new Date().toISOString(),
    enabled: true,
  };

  db.installTool(tool);

  logModification(db, "tool_install", `Installed npm package: ${packageName}`, {
    reversible: true,
  });

  return { success: true };
}

/**
 * Install an MCP server.
 * The automaton can add new capabilities by installing MCP servers.
 */
export async function installMcpServer(
  conway: ConwayClient,
  db: AutomatonDatabase,
  name: string,
  command: string,
  args?: string[],
  env?: Record<string, string>,
): Promise<{ success: boolean; error?: string }> {
  // Record in database
  const tool: InstalledTool = {
    id: ulid(),
    name: `mcp:${name}`,
    type: "mcp",
    config: { command, args, env },
    installedAt: new Date().toISOString(),
    enabled: true,
  };

  db.installTool(tool);

  logModification(
    db,
    "mcp_install",
    `Installed MCP server: ${name} (${command})`,
    { reversible: true },
  );

  return { success: true };
}

/**
 * List all installed tools.
 */
export function listInstalledTools(
  db: AutomatonDatabase,
): InstalledTool[] {
  return db.getInstalledTools();
}

/**
 * Remove (disable) an installed tool.
 */
export function removeTool(
  db: AutomatonDatabase,
  toolId: string,
): void {
  db.removeTool(toolId);
  logModification(db, "tool_install", `Removed tool: ${toolId}`, {
    reversible: true,
  });
}

/**
 * Upstream Awareness
 *
 * Helpers for the automaton to know its own git origin,
 * detect new upstream commits, and review diffs.
 * All git commands run locally via child_process (not sandbox API).
 */

import { execSync } from "child_process";

const REPO_ROOT = process.cwd();

function git(cmd: string): string {
  return execSync(`git ${cmd}`, {
    cwd: REPO_ROOT,
    encoding: "utf-8",
    timeout: 15_000,
  }).trim();
}

/**
 * Return origin URL (credentials stripped), current branch, and HEAD info.
 */
export function getRepoInfo(): {
  originUrl: string;
  branch: string;
  headHash: string;
  headMessage: string;
} {
  const rawUrl = git("config --get remote.origin.url");
  // Strip embedded credentials (https://user:token@host/... -> https://host/...)
  const originUrl = rawUrl.replace(/\/\/[^@]+@/, "//");
  const branch = git("rev-parse --abbrev-ref HEAD");
  const headLine = git('log -1 --format="%h %s"');
  const [headHash, ...rest] = headLine.split(" ");
  return { originUrl, branch, headHash, headMessage: rest.join(" ") };
}

/**
 * Fetch origin and report how many commits we're behind.
 */
export function checkUpstream(): {
  behind: number;
  commits: { hash: string; message: string }[];
} {
  git("fetch origin main --quiet");
  const log = git("log HEAD..origin/main --oneline");
  if (!log) return { behind: 0, commits: [] };
  const commits = log.split("\n").map((line) => {
    const [hash, ...rest] = line.split(" ");
    return { hash, message: rest.join(" ") };
  });
  return { behind: commits.length, commits };
}

/**
 * Return per-commit diffs for every commit ahead of HEAD on origin/main.
 */
export function getUpstreamDiffs(): {
  hash: string;
  message: string;
  author: string;
  diff: string;
}[] {
  const log = git('log HEAD..origin/main --format="%H %an|||%s"');
  if (!log) return [];

  return log.split("\n").map((line) => {
    const [hashAndAuthor, message] = line.split("|||");
    const parts = hashAndAuthor.split(" ");
    const hash = parts[0];
    const author = parts.slice(1).join(" ");
    let diff: string;
    try {
      diff = git(`diff ${hash}~1..${hash}`);
    } catch {
      // First commit in the range may not have a parent
      diff = git(`show ${hash} --format="" --stat`);
    }
    return { hash: hash.slice(0, 12), message, author, diff };
  });
}

import fs from "fs";

export interface EnvironmentInfo {
  type: string;
  sandboxId: string;
}

export function detectEnvironment(): EnvironmentInfo {
  // 1. Check env var
  if (process.env.CONWAY_SANDBOX_ID) {
    return { type: "conway-sandbox", sandboxId: process.env.CONWAY_SANDBOX_ID };
  }

  // 2. Check sandbox config file
  try {
    if (fs.existsSync("/etc/conway/sandbox.json")) {
      const data = JSON.parse(fs.readFileSync("/etc/conway/sandbox.json", "utf-8"));
      if (data.id) {
        return { type: "conway-sandbox", sandboxId: data.id };
      }
    }
  } catch {}

  // 3. Check Docker
  if (fs.existsSync("/.dockerenv")) {
    return { type: "docker", sandboxId: "" };
  }

  // 4. Fall back to platform
  return { type: process.platform, sandboxId: "" };
}

import readline from "readline";
import chalk from "chalk";

let rl: readline.Interface | null = null;

function getRL(): readline.Interface {
  if (!rl) {
    rl = readline.createInterface({
      input: process.stdin,
      output: process.stdout,
    });
  }
  return rl;
}

function ask(question: string): Promise<string> {
  return new Promise((resolve) => {
    getRL().question(question, (answer) => resolve(answer.trim()));
  });
}

export async function promptRequired(label: string): Promise<string> {
  while (true) {
    const value = await ask(chalk.white(`  → ${label}: `));
    if (value) return value;
    console.log(chalk.yellow("  This field is required."));
  }
}

export async function promptMultiline(label: string): Promise<string> {
  console.log("");
  console.log(chalk.white(`  ${label}`));
  console.log(chalk.dim("  Type your prompt, then press Enter twice to finish:"));
  console.log("");

  const lines: string[] = [];
  let lastWasEmpty = false;

  while (true) {
    const line = await ask("  ");
    if (line === "" && lastWasEmpty && lines.length > 0) {
      // Remove the trailing empty line we added
      lines.pop();
      break;
    }
    if (line === "" && lines.length > 0) {
      lastWasEmpty = true;
      lines.push("");
    } else {
      lastWasEmpty = false;
      lines.push(line);
    }
  }

  const result = lines.join("\n").trim();
  if (!result) {
    console.log(chalk.yellow("  Genesis prompt is required. Try again."));
    return promptMultiline(label);
  }
  return result;
}

export async function promptAddress(label: string): Promise<string> {
  while (true) {
    const value = await ask(chalk.white(`  → ${label}: `));
    if (/^0x[0-9a-fA-F]{40}$/.test(value)) return value;
    console.log(chalk.yellow("  Invalid Ethereum address. Must be 0x followed by 40 hex characters."));
  }
}

export function closePrompts(): void {
  if (rl) {
    rl.close();
    rl = null;
  }
}

